test = """     |          
     |  +--+    
     A  |  C    
 F---|----E|--+ 
     |  |  |  D 
     +B-+  +--+ """

class Pos:
  def __init__(self,x,y):
    self.x = x
    self.y = y

  def move(self,delta):
    self.x += delta.dx
    self.y += delta.dy

class Grid:
  def __init__(self,data):
    self.grid = []
    for line in data.split("\n"):
      row = list(line)
      self.grid.append(row)

  def neighbours(self,pos):
    xdim = len(self.grid[0])
    ydim = len(self.grid)
    n =  [self.at(y = pos.y, x = x) for x in [pos.x - 1, pos.x + 1] if 0 <= x < xdim]
    n += [self.at(y = y, x = pos.x) for y in [pos.y - 1, pos.y + 1] if 0 <= y < ydim]
    return n

  def xstart(self):
    return self.grid[0].index("|")

  def at(self,x,y):
    return self.grid[y][x]

def run(data):

  grid = Grid(data)

  dirs = {
  	'D' : Dir(dx = 0, dy = 1),
  	'U' : Dir(dx = 0, dy = -1),
  	'L' : Dir(dx = -1,dy = 0),
  	'R' : Dir(dx = 1, dy = 0)
  }

  pos = Pos(y = 0, x = grid.xstart())
  dir = "D"
  gap = " "
  junction = "+"
  steps = 1

  while True:
    steps += 1
    pos.move(dirs[dir])
    pipe = grid.at(x = pos.x, y = pos.y)
    
    if pipe.isalpha():
      if grid.neighbours(pos).count(gap) == 3:
        return steps
    elif pipe == junction:
      if dir in "UD":
        dir = "R" if grid.at(y = pos.y, x = pos.x - 1) == gap else "L"
      else:
        dir = "D" if grid.at(y = pos.y - 1, x = pos.x) == gap else "U"

print(run(test))
print(run(data))
