#!/usr/bin/env python3 
from itertools import product
from sys import argv
from aoc import AocDay

class AocDay25(AocDay):

    def run_silver(self,data):
        grid = [line for line in data.splitlines()]
        xdim = len(grid[0])
        ydim = len(grid)
        east = set([(x,y) for x,y in product(range(xdim),range(ydim)) if grid[y][x] == ">"])
        south = set([(x,y) for x,y in product(range(xdim),range(ydim)) if grid[y][x] == "v"])
        step = 0
        while True:
            moves = False
            step += 1
            next_east = set()
            for x,y in east:
                x0 = x
                x = (x + 1) % xdim
                if (x,y) in east or (x,y) in south:
                    next_east.add((x0,y))
                else:
                    next_east.add((x,y))
                    moves = True
            east = next_east

            next_south = set()
            for x,y in south:
                y0 = y
                y = (y + 1) % ydim
                if (x,y) in east or (x,y) in south:
                    next_south.add((x,y0))
                else: 
                    next_south.add((x,y))
                    moves = True
            south = next_south

            if not moves: return step

