#!/usr/bin/env python3 
from sys import argv
from aoc import AocDay

class AocDay6(AocDay):

    def detect_unique(self,data,n):
        letters = ""
        for count,c in enumerate(data,start=1):
            letters += c
            if count > n:
                letters = letters[1:]
                if len(set(letters)) == n:
                    return count

    def run_silver(self,data):
        return self.detect_unique(data,4)
        
    def run_gold(self,data):
        return self.detect_unique(data,14)

if __name__ == "__main__":

    silver_tests = ["""bvwbjplbgvbhsrlpgdmjqwftvncz""","""nppdvjthqldpwncqszvftbrmjlhg"""]

    gold_tests = ["""""",""""""]

    data = """"""

    answer = AocDay6(data,silver_tests,gold_tests,argv)

    print(answer)
