from itertools import product

def run_silver(data):

    GRID = data.splitlines()
    XDIM = len(GRID[0])
    YDIM = len(GRID)
    G = {(x,y):cell for x,y in product(range(XDIM),range(YDIM)) if (cell := GRID[y][x]) != "#"}
    START = (1,0)
    END = (XDIM-2,YDIM-1)
    L,R,U,D = (-1,0),(1,0),(0,-1),(0,1)
