#!/usr/bin/env python3 
from itertools import product
from sys import argv
from aoc import AocDay

class AocDay4(AocDay):

    def run_silver(self,data):
        ws = data.splitlines()
        xdim = len(ws[0])
        ydim = len(ws)
        ws = {(x,y):ws[y][x] for x,y in product(range(xdim),range(ydim))}
        answer = 0
        xmas = ["XMAS","SAMX"]
        for x,y in product(range(xdim),range(ydim)):
            words = [
                "".join(ws.get((x+c,y  ),"") for c in range(4)),
                "".join(ws.get((x  ,y+r),"") for r in range(4)),
                "".join(ws.get((x+d,y+d),"") for d in range(4)),
                "".join(ws.get((x+d,y-d),"") for d in range(4))
            ]
            answer += sum(w in xmas for w in words)
        return answer

    def run_gold(self,data):
        DIM = 2
        ws = data.splitlines()
        xdim = len(ws[0])
        ydim = len(ws)
        answer = 0
        xmas = ["MSMS","MSSM","SMMS","SMSM"]
        for x,y in product(range(xdim-DIM),range(ydim-DIM)):
            if ws[y+1][x+1] == "A":
                corners = ws[y][x] + ws[y+DIM][x+DIM] + ws[y+DIM][x] + ws[y][x+DIM]
                answer += (corners in xmas)
        return answer

